import React, { Component } from "react";
import { Form, Icon, Input, Button, Row, Col } from "antd";
import "./index.scss";
import axios from 'axios';
class Registered extends Component {
  handleSubmit = e => {
    e.preventDefault();
    this.props.form.validateFields((err, values) => {
      if (!err) {
        console.log("Received values of form: ", values);
      }
    });
  };

  constructor(props){
    super(props);
    this.handleEmail=this.handleEmail.bind(this);


  }

  handleEmail(){
    axios.post('http://127.0.0.1:3000/email',{
      email:'136565629@qq.com',
  })
  .then((res) => {
      console.log(res);
  })
  .catch((err) => {
      console.log(err);
  })

  }

  render() {
    const { getFieldDecorator } = this.props.form;
    const FormItem = Form.Item;
    const formItemLayout = {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 8 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      }
    };
    return (
      <div className="registered">
        <div className="layout-container">
          <div className="lark-form">
            <div className="lark-form-main">
              <div className="lark-form-logo">
                <a href="/">
                  <img
                    src="https://gw.alipayobjects.com/zos/rmsportal/kyjrtmqaKAvzJAaoZfFp.svg"
                    alt="语雀"
                  />
                </a>
              </div>
              <div className="lark-form-content form-pro">
                <div className="account-slogon">
                  <h2 class="account-slogon-main">欢迎加入博客猿</h2>
                  <p class="account-slogon-subhead">
                    // <span>写文档wppp</span>
                  </p>
                </div>
                <div className="account-register">
                  <Form onSubmit={this.handleSubmit} className="login-form">
                   {/* <FormItem>
                      {getFieldDecorator("userName", {
                        rules: [{ required: true, message: "请输入您的账号!" }]
                      })(
                        <Input
                          prefix={
                            <Icon
                              type="user"
                              style={{ color: "rgba(0,0,0,.25)" }}
                            />
                          }
                          placeholder="用户名"
                        />
                      )}
                    </FormItem>
                        */}
                    <FormItem>
                      {getFieldDecorator("userEmail", {
                        rules: [
                          { required: true, message: "请输入您的邮箱地址!" }
                        ]
                      })(
                        <Input
                          prefix={
                            <Icon
                              type="mail"
                              style={{ color: "rgba(0,0,0,.25)" }}
                            />
                          }
                          placeholder="请输入您的邮箱"
                        />
                      )}
                    </FormItem>
                    <FormItem>
                      <Row>
                        <Col span={14}>
                          <Input placeholder='请输入验证码' />
                        </Col>
                        <Col span={7} offset={1}>
                          <Button type="primary" onClick={this.handleEmail}>获取邮箱验证码</Button>
                        </Col>
                      </Row>
                    </FormItem>
                    <FormItem>
                      {getFieldDecorator("password", {
                        rules: [{ required: true, message: "请输入您的密码!" }]
                      })(
                        <Input
                          prefix={
                            <Icon
                              type="lock"
                              style={{ color: "rgba(0,0,0,.25)" }}
                            />
                          }
                          type="password"
                          placeholder="请输入密码"
                        />
                      )}
                    </FormItem>
                    <FormItem>
                      <a className="login-form-forgot" href="">
                        忘记密码
                      </a>
                      <Button
                        type="primary"
                        htmlType="submit"
                        className="login-form-button"
                      >
                        注册
                      </Button>
                      <a href="">去登录!</a>
                    </FormItem>
                  </Form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Form.create()(Registered);
